import * as saver from './saving';
export module saving{    
    /**
     * Сохранить объект в файл
     * @param obj Сохраняемый объект
     * @param filename Имя файла
     */
    export function SaveObject(obj: Object3, filename: string){
        saver.SaveObject(obj, filename);
    }
    /**
     * Найти объект(ы) по имени и сохранить его в файл. 
     * При сохранении нескольких файлов к имени файла будет добавляться порядковый номер.
     * @param objectName Имя объекта
     * @param filename Имя файла
     * @param firstOnly Сохранять только первый найденный объект
     */
    export function SaveObjectByName(objectName: string, filename: string, firstOnly: boolean){
        saver.SaveObjectByName(objectName, filename, firstOnly);
    }
}